mapper.select:
SELECT o.order_id, o.user_id, o.order_date, o.order_status, o.total_price, m.name AS user_name FROM tb_order o LEFT JOIN tb_member m ON o.user_id = m.user_id WHERE o.order_id IN
#{id}
ORDER BY o.order_date DESC

mapper.update:
UPDATE tb_order
SET order_status = '주문취소'
WHERE order_id = #{order_id}

UPDATE tb_order_item
SET status = '주문취소'
WHERE order_id = #{order_id}

