mapper.update:
UPDATE tb_product_qna
SET QNA_STATUS = #{qnaStatus}
WHERE QNA_ID = #{qnaId}

mapper.select:
SELECT q.QNA_ID as qnaId, q.PRODUCT_ID as productId, p.NAME as productName, q.USER_ID as userId, q.TITLE as title, q.CONTENT as content, q.QNA_STATUS as qnaStatus, q.IS_SECRET as isSecret, q.VIEW_COUNT as viewCount, q.ANSWER_DATE as answerDate, q.ANSWER_USER_ID as answerUserId, q.CREATED_DATE as createdDate, q.UPDATED_DATE as updatedDate FROM tb_product_qna q JOIN tb_product p ON q.PRODUCT_ID = p.PRODUCT_ID WHERE p.HOST_ID = #{hostId}
AND ( LOWER(p.NAME) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(q.TITLE) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(q.CONTENT) LIKE CONCAT('%', LOWER(#{keyword}), '%') )
ORDER BY q.CREATED_DATE DESC
